function mos_btt_admin_page() {
	if ( ! current_user_can( 'manage_options' ) ) {
		return;
	}
	if ( isset( $_GET['settings-updated'] ) ) {
		add_settings_error( 'mos_btt_messages', 'mos_btt_message', __( 'Settings Saved', 'mos_btt' ), 'updated' );
	}
	settings_errors( 'mos_btt_messages' );
	?>
	<div class="wrap mos-btt-wrapper">
		<h1><?php echo esc_html( get_admin_page_title() ); ?></h1>
		<form action="options.php" method="post">
		<?php
		settings_fields( 'mos_btt' );
		do_settings_sections( 'mos_btt' );
		submit_button( 'Save Settings' );
		?>
		</form>
	</div>
	<?php
}